import React, { useState, useEffect } from "react";
import axios from "axios";
import Page from "../components/Page";
import SongsListGroup from "../components/SongsListGroup";
import SongsTable from "../components/SongsTable";
import { Col, Container, Row } from "react-bootstrap";
import useAuth from "../hooks/useAuth";

const Songs = () => {
  const [getApiSongs, setApiSongs] = useState([]);
  const [allSongs, setAllSongs] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState("All Artists");
  const [pageNumber, setPageNumber] = useState(0);
  const [sortColumn, setSortColumn] = useState({
    path: "",
    order: "ascending",
  });

  const { user, handleLogout } = useAuth();

  useEffect(() => {
    function getSongsData() {
      return axios
        .get("http://localhost:8080/api/songs", {
          withCredentials: true,
        })
        .then((response) => {
          const { data } = response;
          setAllSongs(data);
          setApiSongs(data);
          return data;
        })
        .catch((err) => {
          const { status } = err.response;
          console.log(err);
          if (status === 401 || status === 403) {
            window.location.reload();
          }
          if (status === 400) {
            alert("Unauthorized");
            window.location.reload();
            handleLogout();
          } else {
            window.location.reload();
            alert("An internal server error occurred.");
            handleLogout();
          }
          return status;
        });
    }
    getSongsData();
  }, [user]);

  useEffect(() => {
    setAllSongs(getApiSongs);
    setPageNumber(0);
    setSortColumn({ path: "", order: "ascending" });
  }, [selectedArtist]);

  // const getSongsData = async () => {
  //   await axios
  //     .get("http://localhost:8080/api/songs")
  //     .then((response) => {
  //       const songsData = response.data;
  //       setAllSongs(songsData);
  //       setApiSongs(songsData);
  //     })
  //     .catch((err) => {
  //       const { status } = err.response;
  //       console.log(err);
  //       if (status === 401 || status === 403) {
  //         window.location.reload();
  //       }
  //       if (status === 400) {
  //         alert("Unauthorized");
  //         window.location.reload();
  //         handleLogout();
  //       } else {
  //         window.location.reload();
  //         alert("An internal server error occurred.");
  //         handleLogout();
  //       }
  //     });
  // };

  return (
    <Page title="Tabled Data">
      <Container fluid style={{ maxWidth: "2000px" }}>
        <Row>
          <Col xs={3} md={2}>
            <SongsListGroup
              onArtistSelect={setSelectedArtist}
              selectedArtist={selectedArtist}
            />
          </Col>
          <Col xs={9} md={10}>
            <SongsTable
              allSongs={allSongs}
              setAllSongs={setAllSongs}
              selectedArtist={selectedArtist}
              pageNumber={pageNumber}
              setPageNumber={setPageNumber}
              sortColumn={sortColumn}
              setSortColumn={setSortColumn}
            />
          </Col>
        </Row>
      </Container>
    </Page>
  );
};

export default Songs;
