import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "universal-cookie";
const cookies = new Cookies();

// const authedSession = cookies.get("authedSession");
const authedToken = cookies.get("authedToken");

const useAuth = () => {
  const [isUser, setIsUser] = useState(cookies.get("authedSession"));
  const [user, setUser] = useState(() => {
    if (!isUser) return "";
    return localStorage.getItem("user");
  });

  // useEffect(() => {
  //   if (authedToken && authedSession) {
  //     const currentUser = JSON.parse(localStorage.getItem("user"));
  //     setUser(currentUser);
  //     setIsUser(authedSession);
  //   } else if (authedToken && !authedSession) {
  //     console.log("refreshing of tokens needed!");
  //     const currentUser = JSON.parse(localStorage.getItem("user"));
  //     setUser(currentUser);
  //     setIsUser(authedSession);
  //   } else {
  //     setUser();
  //     setIsUser(false);
  //   }
  // }, []);

  const handleLogin = async (username, password, error) => {
    try {
      const response = await axios.post(
        "/api/login",
        JSON.stringify({ username, password }),
        {
          headers: { "Content-Type": "application/json" },
        }
      );

      localStorage.setItem("user", JSON.stringify(response.data.user));

      // setUser(response.data.user);
      setIsUser(true);
    } catch (err) {
      if (!err?.response) {
        error = "No Server Response";
        // setErrorMsg("No Server Response");
      } else if (err.response?.status === 400) {
        // setErrorMsg("Missing Username or Password");
        error = "Missing Username or Password";
      } else if (err.response?.status === 401) {
        error = "Missing Username or Password";
        // setErrorMsg("Incorrect username or password");
        // console.log("Incorrect username or password");
      } else {
        error = "Login Failed";
        // setErrorMsg("Login Failed");
      }
      return error;
    }
  };

  const handleLogout = async () => {
    await axios
      .delete("/api/logout", user, {
        headers: { "Content-Type": "application/json" },
      })
      .then(() => {
        localStorage.clear();
        console.log("Logged out successfully.");
      })
      .catch(() => {
        console.log("An internal server error has occurred.");
      });

    localStorage.clear();
    setUser("");
    setIsUser(false);
  };

  return {
    user,
    isUser,
    setUser,
    setIsUser,
    handleLogin,
    handleLogout,
  };
};

export default useAuth;
