import { useState, useEffect } from "react";
import { omit } from "lodash";

const useContact = (callback) => {
  const [values, setValues] = useState({});
  const [errors, setErrors] = useState({});
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    setSubmitted(false);
  }, [values]);

  const validate = (name, value) => {
    switch (name) {
      case "username":
        if (value.length <= 1) {
          setErrors({
            ...errors,
            username: "Your name must contain at least 2 letters",
          });
        } else {
          let newObj = omit(errors, "username");
          setErrors(newObj);
        }

        break;

      case "email":
        if (
          !new RegExp(
            /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
          ).test(value)
        ) {
          setErrors({
            ...errors,
            email: "Please enter a valid email (ex: johndoe@email.com)",
          });
        } else {
          let newObj = omit(errors, "email");
          setErrors(newObj);
        }
        break;
      default:
        break;
    }
  };

  const handleChange = (e) => {
    e.persist();

    let name = e.target.name;
    let val = e.target.value;

    validate(name, val);

    setValues({
      ...values,
      [name]: val,
    });
  };

  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    if (Object.keys(errors).length === 0) {
      setSubmitted(true);
      console.log("Form Values: ", values);
      try {
        await axios.post("http://localhost:8080/api/save", values, {
          headers: { "Content-Type": "application/json" },
          withCredentials: true,
        });
        alert("Your response was submitted, thank you for reaching out!");
        window.location.reload();
        console.log("Data has been sent to the server");
      } catch (error) {
        if (error.response.status === 401) {
          window.location.reload();
          alert(
            "Your session expired before submitting the form - reauthentication successful. Please resubmit the form."
          );
          return;
        }
        if (error.response.status === 403) {
          alert("Forbidden");
          window.location.reload();
        } else {
          alert("An internal server error occurred.");
          window.location.reload();
        }
      }
      // callback();
    } else {
      alert(
        "Form was not submitted. Make sure required fields were filled correctly."
      );
    }
  };

  return {
    values,
    errors,
    submitted,
    handleChange,
    handleSubmit,
  };
};

export default useContact;
