import { useState, useEffect } from "react";
import { omit } from "lodash";

const useLogin = (callback) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState("");
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    setErrors(errors);
    console.log("COMING FROM useLogin, errors: ", errors);
    setSubmitted(false);
  });

  // const validate = (username, password) => {
  //   if (!new RegExp(/^[A-Za-z-0-9]+$/).test(username)) {
  //     setErrors("Incorrect username or password");
  //   } else setErrors("");

  //   if (
  //     !new RegExp(/^[A-Za-z-0-9]+$/).test(password) &&
  //     password.length !== 12
  //   ) {
  //     setErrors("Incorrect username or password");
  //   } else setErrors("");
  // };
  const validate = (name, value) => {
    switch (name) {
      case "username":
        if (!new RegExp(/^[A-Za-z]+$/).test(value)) {
          setErrors("Incorrect username or password");
        } else setErrors("");
        break;

      case "password":
        if (!new RegExp(/^[A-Za-z-0-9]+$/).test(value) && value.length !== 12) {
          setErrors("Incorrect username or password");
        } else setErrors("");
        break;
      default:
        break;
    }
  };

  const handleChange = (e) => {
    e.persist();

    let name = e.target.name;
    let val = e.target.value;

    validate(name, val);
    console.log(errors);

    if (name === "username") setUsername(val);
    if (name === "password") setPassword(val);
  };

  const handleSubmit = (e) => {
    if (e) e.preventDefault();

    // validate(username, password);
    // console.log(errors);

    if (!errors) {
      setSubmitted(true);
      callback();
    } else {
      alert(
        "Form was not submitted. Make sure required fields were filled correctly."
      );
    }
  };

  return {
    username,
    password,
    errors,
    submitted,
    setUsername,
    setPassword,
    handleChange,
    handleSubmit,
  };
};

export default useLogin;
