import { useState, useEffect } from "react";
import { omit } from "lodash";

const useLogin = (callback) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isError, setError] = useState(false);

  useEffect(() => {
    // setError(isError);
    console.log("COMING FROM useLogin, isError: ", isError);
  }, [isError]);

  const validate = (name, value) => {
    switch (name) {
      case "username":
        if (!new RegExp(/^[A-Za-z-0-9]+$/).test(value) || value.length !== 8) {
          setError(true);
        } else setError(...isError, false);
        break;

      case "password":
        if (!new RegExp(/^[A-Za-z-0-9]+$/).test(value) || value.length !== 12) {
          setError(true);
        } else setError(...isError, false);
        break;
      default:
        break;
    }
  };

  const handleChange = (e) => {
    e.persist();

    let name = e.target.name;
    let val = e.target.value;

    validate(name, val);
    console.log("COMING FROM useLogin's handleChange, isError: ", isError);

    if (name === "username") setUsername(val);
    if (name === "password") setPassword(val);
  };

  const handleSubmit = (e) => {
    if (e) e.preventDefault();

    if (!isError) callback();
    else {
      alert(isError);
      window.location.reload();
    }
  };

  return {
    username,
    password,
    setUsername,
    setPassword,
    handleChange,
    handleSubmit,
  };
};

export default useLogin;
