import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "universal-cookie";

const cookies = new Cookies();
const isAuthed = cookies.get("isAuthed");
const activeUser = cookies.get("user");

axios.defaults.withCredentials = true;

const useAuth = () => {
  const [isUser, setUser] = useState(isAuthed);
  const checkUser = localStorage.getItem("user");

  useEffect(() => {
    let mounted = true;
    async function getData() {
      await axios
        .post("http://localhost:8080/api/auth", {
          isAuthed,
        })
        .then((res) => {
          if (mounted && res) {
            setUser(true);
            console.log("NOOOOOO");
          }
        })
        .catch(() => {
          if (checkUser) {
            handleLogout();
            window.location.reload();
            alert("Unauthorized");
          }
          mounted = false;
        });
    }
    getData();
    return () => {
      if (!mounted) {
        window.location.reload();
      }
      console.log("HERE!!! ", mounted);
      mounted = false;
    };
  }, []);

  // useEffect(() => {
  //   let mounted = true;
  //   if (mounted) {
  //     if (checkUser) {
  //       console.log("checkUser: ", checkUser);
  //       const currentUser = JSON.parse(checkUser);
  //       setUser(currentUser);
  //     } else {
  //       setUser();
  //     }
  //   }
  //   return () => {
  //     mounted = false;
  //   };
  // }, [isUser]);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(
        "http://localhost:8080/api/login",
        JSON.stringify({ username, password }),
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true,
        }
      );
      let name = JSON.stringify(response.data.user);
      localStorage.setItem("user", name);
      window.location.reload();
      setUser(true);
    } catch (err) {
      if (!err?.response) {
        alert("An unexpected error occurred. Please try again later.");
      } else if (err.response?.status === 400) {
        alert("Missing Username or Password");
      } else if (err.response?.status === 401) {
        alert("Incorrect username or password");
        console.log("Incorrect username or password");
      } else {
        alert("Login Failed");
      }
    }
  };

  const handleLogout = () => {
    axios.delete("http://localhost:8080/api/clear-cookies");
    axios
      .get("http://localhost:8080/api/logout", {
        headers: { "Content-Type": "application/json" },
        withCredential: true,
      })
      .then(() => {
        localStorage.clear();
        // window.location.reload();
      })
      .catch(() => {
        console.log("An internal server error has occurred.");
      });

    localStorage.clear();
    window.location.reload();
  };

  return {
    isUser,
    activeUser,
    setUser,
    handleLogin,
    handleLogout,
  };
};

export default useAuth;

