import { useState, useEffect } from "react";
import axios from "axios";

// TODO:
//
// This is a custom hook that will communicate with App.js, Login.jsx, and Header.jsx.
//
// App.js:
//
//
// Login.jsx:
// isUser/setIsUser and handle submit with axios call.
//
// Header.jsx:
// user/setUser and take care of logout.

const useAuth = () => {
  const [isUser, setIsUser] = useState(false || localStorage.getItem("user"));
  const [user, setUser] = useState(() => {
    if (!isUser) return "";
    return localStorage.getItem("user");
  });

  useEffect(() => {
    const checkUser = localStorage.getItem("user");
    if (checkUser) {
      console.log("checkUser: ", checkUser);
      const currentUser = JSON.parse(checkUser);
      setUser(currentUser);
      setIsUser(true);
    } else {
      setUser();
      setIsUser(false);
    }
  }, []);

  // useEffect(() => {
  //   const activeUser = user;
  //   console.log("Logged in user: ", activeUser);
  // }, [user]);

  return {
    user,
    isUser,
    setUser,
    setIsUser,
  };
};

export default useAuth;
