import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "universal-cookie";
const cookies = new Cookies();

// const authedSession = cookies.get("authedSession");
// const authedToken = cookies.get("authedToken");

const useAuth = () => {
  const [isUser, setIsUser] = useState(false || localStorage.getItem("user"));
  const [user, setUser] = useState(() => {
    if (!isUser) return "";
    return localStorage.getItem("user");
  });

  useEffect(() => {
    const checkUser = localStorage.getItem("user");
    if (checkUser) {
      console.log("checkUser: ", checkUser);
      const currentUser = JSON.parse(checkUser);
      setUser(currentUser);
      setIsUser(true);
    } else {
      setUser();
      setIsUser(false);
    }
  }, []);

  const handleLogout = () => {
    axios.delete("/api/clear-cookies");
    axios
      .get("/api/logout", {
        headers: { "Content-Type": "application/json" },
      })
      .then(() => {
        localStorage.clear();
        console.log("Logged out successfully.");
      })
      .catch(() => {
        console.log("An internal server error has occurred.");
      });

    localStorage.clear();
    // setUser();
    setUser(false);
  };

  return {
    user,
    isUser,
    setUser,
    setIsUser,
    // handleLogin,
    handleLogout,
  };
};

export default useAuth;
