        sameSite: "strict",
        secure: true,
      }),
      ctx.cookie("isAuthed", true, {
        expires: new Date(new Date().getTime() + 31557600000),
        origin: "http://localhost:3000",
        sameSite: "strict",
        secure: true,
      }),
      ctx.status(202),
      ctx.json({ user })
