import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "universal-cookie";
const cookies = new Cookies();

// const authedSession = cookies.get("authedSession");
// const authedToken = cookies.get("authedToken");

const useAuth = () => {
  const [isUser, setIsUser] = useState(cookies.get("authedSession"));
  const [user, setUser] = useState(() => {
    if (!isUser) return "";
    return localStorage.getItem("user");
  });

  useEffect(() => {
    const checkUser = localStorage.getItem("user");
    if (checkUser) {
      console.log("checkUser: ", checkUser);
      const currentUser = JSON.parse(checkUser);
      setUser(currentUser);
      setIsUser(true);
    } else {
      setUser();
      setIsUser(false);
    }
  }, []);

  // useEffect(() => {
  //   const activeUser = user;
  //   console.log("Logged in user: ", activeUser);
  // }, [user]);

  return {
    user,
    isUser,
    setUser,
    setIsUser,
  };
};

export default useAuth;
