import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "universal-cookie";
const cookies = new Cookies();

// const authedSession = cookies.get("authedSession");
// const authedToken = cookies.get("authedToken");

const useAuth = () => {
  const [isUser, setIsUser] = useState(false);
  const [user, setUser] = useState("");

  // useEffect(() => {

  // }, [isUser]);

  // const [isUser, setIsUser] = useState(() => {
  //   if (authedSession) return true;
  //   return false;
  // });
  // const [user, setUser] = useState(localStorage.getItem("user"));
  // const [user, setUser] = useState(() => {
  //   if (!isUser) return "";
  //   return localStorage.getItem("user");
  // });

  // useEffect(() => {
  //   if (authedSession)
  // }, [])

  // useEffect(() => {
  //   if (authedSession) {
  //     const currentUser = JSON.parse(localStorage.getItem("user"));
  //     setUser(currentUser);
  //     setIsUser(authedSession);
  //   } else {
  //     setUser();
  //     setIsUser(false);
  //   }
  // }, []);

  console.log("From useAuth isUser - ", isUser);
  console.log("From useAuth user - ", user);

  const handleLogin = async () => {
    try {
      await axios.post("/api/login", JSON.stringify({ username, password }), {
        headers: { "Content-Type": "application/json" },
      });

      localStorage.setItem("user", username);
      // localStorage.setItem("user", JSON.stringify(response.data.user));

      // setUser(response.data.user);
      setUser(localStorage.getItem("user"));
      setIsUser(true);
    } catch (err) {
      if (!err?.response) {
        setErrorMsg("No Server Response");
      } else if (err.response?.status === 400) {
        setErrorMsg("Missing Username or Password");
      } else if (err.response?.status === 401) {
        setErrorMsg("Incorrect username or password");
        console.log("Incorrect username or password");
      } else {
        setErrorMsg("Login Failed");
      }
    }
  };

  const handleLogout = async () => {
    await axios
      .delete("/api/logout", user, {
        headers: { "Content-Type": "application/json" },
      })
      .then(() => {
        // localStorage.clear();
        console.log("Logged out successfully.");
      })
      .catch(() => {
        console.log("An internal server error has occurred.");
      });

    // localStorage.clear();
    setUser("");
    setIsUser(false);
    // window.location.reload();
  };

  return {
    user,
    isUser,
    setUser,
    setIsUser,
    handleLogin,
    handleLogout,
  };
};

export default useAuth;
