import { useState, useEffect } from "react";
import { omit } from "lodash";

const useLogin = (callback) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState("");

  useEffect(() => {
    // setErrors(errors);
    console.log("COMING FROM useLogin, errors: ", errors);
  }, [errors]);

  const validate = (name, value) => {
    switch (name) {
      case "username":
        if (!new RegExp(/^[A-Za-z-0-9]+$/).test(value) || value.length !== 8) {
          setErrors("Incorrect username or password");
        } else setErrors("");
        break;

      case "password":
        if (!new RegExp(/^[A-Za-z-0-9]+$/).test(value) || value.length !== 12) {
          setErrors("Incorrect username or password");
        } else setErrors("");
        break;
      default:
        break;
    }
  };

  const handleChange = (e) => {
    e.persist();

    let name = e.target.name;
    let val = e.target.value;

    validate(name, val);
    console.log(errors);

    if (name === "username") setUsername(val);
    if (name === "password") setPassword(val);
  };

  const handleSubmit = (e) => {
    if (e) e.preventDefault();

    if (!errors) callback();
    else {
      alert(errors);
      window.location.reload();
    }
  };

  return {
    username,
    password,
    errors,
    setUsername,
    setPassword,
    handleChange,
    handleSubmit,
  };
};

export default useLogin;
