import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "universal-cookie";
const cookies = new Cookies();

var authedSession = cookies.get("authedSession");
var authedToken = cookies.get("authedToken");

const useAuth = () => {
  const [isUser, setIsUser] = useState(authedSession);
  const [user, setUser] = useState(() => {
    if (!isUser) return "";
    return localStorage.getItem("user");
  });

  useEffect(() => {
    if (authedToken && authedSession) {
      const currentUser = JSON.parse(localStorage.getItem("user"));
      setUser(currentUser);
      setIsUser(authedSession);
    } else if (authedToken && !authedSession) {
      console.log("refreshing of tokens needed!");
      const currentUser = JSON.parse(localStorage.getItem("user"));
      setUser(currentUser);
      setIsUser(authedSession);
    } else {
      setUser();
      setIsUser(false);
    }
  }, []);

  const handleLogout = async () => {
    await axios
      .get("/api/logout", user, {
        headers: { "Content-Type": "application/json" },
      })
      .then(() => {
        localStorage.clear();
        console.log("Logged out successfully.");
      })
      .catch(() => {
        console.log("An internal server error has occurred.");
      });

    localStorage.clear();
    setUser("");
    setIsUser(false);
  };

  return {
    user,
    isUser,
    setUser,
    setIsUser,
    handleLogout,
  };
};

export default useAuth;
